import React, { Component } from 'react';
import axios from 'axios';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import { Link } from 'react-router-dom';
import Header from '../menu'

export default class PropAtelier extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }


    componentDidMount() {
        axios.get(`http://localhost:8080/api/users/newEleve`)
            .then(response => {
                console.log('user-article ==== ', response.data)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })

    }

    liste() {
        return <div>
           <Header/>
        <div className="card" >

            <div className="container-fluid">
                {
                    (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {

                        return <div key={obj._id} className="container-fluid">
                            <center>
                                <img width="200px" height="100px" src={'http://localhost:8080/api/users/newEleveImage/' + obj.image} alt="pdp" />
                            </center>
                            <center>
                            <div className="row"> 
                                <div className ="col-md-6">
                                    <span></span><p>{obj.nom}</p>
                                   <span></span><p id="des">{obj.prenom}</p>
                                </div>
                                 <div className ="col-md-6">
                                    <span></span><p>{obj.age} </p>
                                    <span></span><p>{obj.classe} </p>
                                 </div>
                                <span></span><p>{obj.sexe}</p>
                             </div>
                            </center>

                        </div>

                    })) : ('')
                }
            </div>
        </div>
       </div>
    }
    render() {
        return (
            <div className='app'>
                {this.liste()}
            </div>
        );
    }
}
