import React, { Component } from 'react';
import axios from 'axios';

import { Link } from 'react-router-dom';

export default class PropAtelier extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {
        axios.get(`http://localhost:8080/api/users/newArticle/${localStorage.id}`)
            .then(response => {
                console.log('user-article ==== ', response)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <table className="table table-bordered" id="tableau">
            <thead>
                <tr>
                    <th>titre</th>
                    <th>description</th>
                    <th>photo</th>
                    <th>Heure</th>
                    <th>Date</th>
                    <th>actions</th>
                </tr>
            </thead>
            <tbody>
                {
                    (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {

                        return <tr key={obj._id}>
                            <td>{obj.titre}</td>
                            <td id="des">{obj.description}</td>
                            <td>
                                <img width="150px" height="50px" src={'http://localhost:8080/api/users/newArticleImage/' + obj.image} alt="pdp" />
                            </td>
                            <td>{obj.debut} </td>
                            <td>{obj.date}</td>
                            <td>
                                <Link to={"/modifierAtl/" + obj._id}  class="btn btn-warning">Modifier</Link>

                            {obj.visib == true ? (<button  class="btn btn-success" onClick={(e) => {
                                e.preventDefault()
                                axios.get(" http://localhost:8080/api/users/cacherAtl/" + obj._id).then(res => {
                                    axios.get('http://localhost:8080/api/users/newArticle/' + localStorage.id).then(res => {
                                        console.log(res.data)
                                        this.setState({ profil: res.data })
                                    })
                                    console.log(res.data)
                                })


                            }}>Activer</button>) : (<button   class="btn btn-warning" onClick={(e) => {
                                e.preventDefault()
                                console.log(obj._id)
                                axios.get("http://localhost:8080/api/users/affichAtl/" + obj._id).then(res => {
                                    axios.get('http://localhost:8080/api/users/newArticle/' + localStorage.getItem('id')).then(res => {
                                        console.log(res.data)
                                        this.setState({ profil: res.data })
                                    })
                                    console.log(res.data)
                                })

                            }}>desactiver</button>)}</td>
                             <button class="btn btn-success" onClick={() => {
                                            confirmAlert({
                                                customUI: ({ onClose }) => {
                                                    return (
                                                        <center>
                                                            <div id="mail" className='custom-ui'>
                                                                <div >
                                                                    <h4> {obj.Titre}</h4>
                                                                    <input name="nom" onChange={this.handleChange}
                                                                        value={this.state.value} placeholder="Nom" /><br></br>
                                                                    <input name="prenom" placeholder="Prenom" onChange={this.handleChange} value={this.state.value} /><br></br>
                                                                    <input name="phone" placeholder="Numero téléphone" onChange={this.handleChange} value={this.state.value} /><br></br>
                                                                    <input name="email" placeholder="Email" onChange={this.handleChange} value={this.state.value} /><br></br>

                                                                    <button type="button" class="btn btn-warning" onClick={() => {
                                                                        axios.post("http://localhost:8080/api/users/particulier/" + obj._id, {
                                                                            nom: this.state.nom,
                                                                            prenom: this.state.prenom,
                                                                            phone: this.state.phone,
                                                                            email: this.state.email

                                                                        }).then(res => {
                                                                            console.log(res.data);
                                                                            axios.get("http://localhost:8080/api/users/newArticle").then(res => {

                                                                                this.setState({ profil: res.data })
                                                                                console.log(this.state.profil)

                                                                            })
                                                                        })

                                                                        onClose();
                                                                    }}
                                                                    >
                                                                        confirmer
                                                                </button>
                                                                    <button type="button" class="btn btn-danger" onClick={onClose}>Annuler</button>
                                                                </div>

                                                            </div></center>
                                                    );
                                                }
                                            });
                                        }} >S'inscrire</button>
                        </tr>

                    })) : ('')
                }
            </tbody>
        </table>
    }
    render() {
        return (
            <div className='app1'>
                {this.liste()}
            </div>
        );
    }
}
