import React, { Component } from 'react';
import axios from 'axios';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import { Link } from 'react-router-dom';
import Header from '../menu'

export default class PropAtelier extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }


    componentDidMount() {
        axios.get(`http://localhost:8080/api/users/newEleve`)
            .then(response => {
                console.log('user-article ==== ', response.data)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })

    }

    liste() {
        return <div>
           <Header/>
        <table className="table table-bordered" >
            <thead>
                <tr>
                    <th>Nom</th>
                    <th>Prenom</th>
                    <th>Age</th>
                    <th>Classe</th>
                    <th>Image</th>
                    <th>Sexe</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {
                    (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {

                        return <tr key={obj._id}>
                            <td>{obj.nom}</td>
                            <td id="des">{obj.prenom}</td>
                            <td>{obj.age} </td>
                            <td>{obj.classe} </td>
                            <td>
                                <img width="150px" height="50px" src={'http://localhost:8080/api/users/newArticleImage/' + obj.image} alt="pdp" />
                            </td>

                            <td>{obj.sexe}</td>
                            <td>
                                <Link to={"/modifierAtl/" + obj._id}  class="btn btn-warning">Modifier</Link>

                            </td>

                        </tr>

                    })) : ('')
                }
            </tbody>
        </table>
       </div>
    }
    render() {
        return (
            <div className='app'>
                {this.liste()}
            </div>
        );
    }
}
